mapper.select:
select a.id,b.head_id,c.biz_id, b.biz_type,c.biz_date, a.sku_id,x.store_id, b.serial_id,c.serial,c.serial1,c.serial2,c.serial3,c.qty,a.gsdm ,'0' as del_flag from biz_purchase_in as x inner join biz_purchase_in_detail as a on x.id = a.head_id and x.gsdm = #{gsdm} inner join biz_flow_serial as b on a.id = b.head_id and a.del_flag = '0' and b.del_flag = '0' inner join biz_serial as c on b.serial_id = c.id and b.del_flag = '0' and b.gsdm = #{gsdm}
where 1=1
and a.id in
#{item}
order by b.id
